$main-text-color: white;
$main-bg-color: black;
$add-bg-color: blanchedalmond;

* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    scroll-behavior: smooth;
}

header {
    background-color: $main-bg-color;
    color: $main-text-color;
    font-family: "Dosis", sans-serif;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 2rem 5%;
    h1 {
        cursor: pointer;
    }
    .nav-links {
        list-style: none;
        li {
            display: inline-block;
            padding: 0 1rem;
        }
        li,
        a {
            font-size: 24px;
            text-decoration: none;
            color: $main-text-color;
            transition: all 0.3s ease 0s;
        }
        li a:hover {
            color: lightblue;
        }
    }
}

.weather-forecast {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background: #222;
    background-image: url("https://source.unsplash.com/1600x900/?landscape");
    font-family: "Dosis", sans-serif;
    .weather-card {
        background-color: #000000d0;
        color: $main-text-color;
        padding: 2rem;
        border-radius: 2rem;
        width: 100%;
        max-width: 420px;
        margin: 1rem;
        .search {
            display: flex;
            justify-content: center;
            align-items: center;
            margin-bottom: 1rem;
            input.search-bar {
                width: calc(100% - 4rem);
                border-radius: 24px;
                border: none;
                outline: none;
                padding: 0.4rem 1rem;
                font-family: inherit;
                font-size: 110%;
            }
            button {
                margin: 0.5rem;
                border-radius: 50%;
                border: none;
                outline: none;
                height: 2.2rem;
                width: 2.2rem;
                cursor: pointer;
                transition: 0.2s ease-in-out;
            }
            button:hover {
                background-color: lightgray;
            }
        }
        .weather {
            font-size: 120%;
            .temperature {
                margin-bottom: 0.5rem;
            }
            .icon-and-description {
                display: flex;
                align-items: center;
                .description {
                    text-transform: capitalize;
                    margin-left: 0.5rem;
                }
            }
        }
    }
}

.photos {
    height: 100%;
    background-color: $add-bg-color;
    font-family: "Dosis", sans-serif;
    padding: 2rem 5%;
    h1 {
        text-align: center;
        font-size: 28px;
        margin-bottom: 2rem;
    }
    .photo-cards {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;
        .photo-card {
            min-width: 20rem;
            background-color: $main-text-color;
            width: 30%;
            height: 240px;
            margin: 1rem;
            text-align: center;
        }
    }
}

.the-end {
    height: 40%;
    font-family: "Dosis", sans-serif;
    padding: 2rem 5%;
    h1 {
        text-align: center;
        font-size: 28px;
        margin-bottom: 2rem;
    }
    .end-container {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;
        div {
            width: 30%;
            text-align: center;
            font-size: 24px;
            margin: 1rem;
            background-color: $add-bg-color;
            padding: 0.5rem;
            max-height: 5rem;
        }
    }
}

footer {
    background-color: $main-bg-color;
    color: $main-text-color;
    font-family: "Dosis", sans-serif;
    padding: 1rem 5%;
    font-size: 20px;
    i{
        color: $main-text-color;
    }
    .socialmedia-and-copyright {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-wrap: wrap;
        .social {
            flex: 1;
        }
        .copyright {
            margin-right: 2rem;
        }
    }
}
